CREATE EXTENSION IF NOT EXISTS postgis;

-- Create the 'capture' schema if it does not exist
CREATE SCHEMA IF NOT EXISTS capture;

ALTER SCHEMA capture OWNER TO postgres;

-- Create the table
CREATE TABLE IF NOT EXISTS capture.waterservices_timeseries_metadata (
    id UUID PRIMARY KEY,
    unit_of_measure VARCHAR(50),
    parameter_name VARCHAR(255),
    parameter_code VARCHAR(50),
    statistic_id VARCHAR(50),
    last_modified TIMESTAMP,
    "begin" TIMESTAMP,
    "end" TIMESTAMP,
    computation_period_identifier VARCHAR(50),
    computation_identifier VARCHAR(50),
    thresholds JSONB,  -- Storing as JSONB for flexibility
    sublocation_identifier VARCHAR(50),
    "primary" VARCHAR(50),
    monitoring_location_id VARCHAR(50),
    geometry GEOMETRY(POINT, 4326)
);

